export function calculateWinner(squares) {
    const lines = [
        [0, 1, 2, 3],
        [1, 2, 3, 4],
        [5, 6, 7, 8],
        [6, 7, 8, 9],
        [10, 11, 12, 13],
        [11, 12, 13, 14],
        [15, 16, 17, 18],
        [16, 17, 18, 19],
        [20, 21, 22, 23],
        [21, 22, 23, 24],
        [0, 5, 10, 15],
        [5, 10, 15, 20],
        [1, 6, 11, 16],
        [6, 11, 16, 21],
        [2, 7, 12, 17],
        [7, 12, 17, 22],
        [3, 8, 13, 18],
        [8, 13, 18, 23],
        [4, 9, 14, 19],
        [9, 14, 19, 24],
        [0, 6, 12, 18],
        [1, 7, 13, 19],
        [2, 8, 14, 20],
        [3, 7, 11, 15],
        [4, 8, 12, 16],
        [5, 11, 17, 23],
        [6, 12, 18, 24],
        [9, 13, 17, 21],
        [10, 14, 18, 22]
    ];
    for (let i = 0; i < lines.length; i++) {
        const [a, b, c, d] = lines[i];
        if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c] && squares[a] === squares[d]) {
            return squares[a];
        }
    }
    return null;
}

var concat = function(a, b, c, d, e, board) {
    var result = board[a] + board[b] + board[c] + board[d] + board[e];
    
    switch (result) {
        case "xxxxx":
            return ["x", e];
            break;
        case "xxxnull":
            return ["x", c];
            break;
        case "xxnullx":
            return ["x", b];
            break;
        case "xnullxx":
            return ["x", a];
            break;
        case "nullxxx":
            return ["x", e];
            break;
        case "nullxx":
            return ["x", c];
            break;
        case "nulx":
            return ["x", b];
            break;
        case "nullx":
            return ["x", a];
            break;
        case "ooooo":
            return ["o", e];
            break;
        case "ooonull":
            return ["o", c];
            break;
        case "oonullo":
            return ["o", b];
            break;
        case "onull":
            return ["o", a];
            break;
        case "nullooo":
            return ["o", e];
            break;
        case "nulloo":
            return ["o", c];
            break;
        case "nullo":
            return ["o", b];
            break;
        case "null":
            return ["o", a];
            break;
        default:
            return ["k", a];
            break;
    }
}

export function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
    //Максимум не включается, минимум включается
}

export function getNextStep(board){
    for (let i = 20; i >= 0; i--){
        if (!board[i]) {
            if (i < 5 || (board[i + 5] && (board[i + 5] === "x" || board[i + 5] === "o"))) {
                return i;
            }
        }
    }
    return getRandomInt(0, 5);
}
